import { useState} from "react";
const Tab =({info,page_id,handleClick,editTab,deleteTab,doZoom=true})=>{
const [isOpen, setIsOpen ] = useState(false);

  return <div className='Tab' onClick={handleClick} onMouseEnter={()=> setIsOpen(true)}  onMouseLeave={()=> setIsOpen(false)} >
<h5>{info.page_num}</h5>
{info.page_id === page_id || (isOpen && doZoom )?
  <>
<p>{info.text}</p>
<button type="button" onClick={editTab} name="button">Edit</button>
<button type="button" onClick={deleteTab} name="button">X</button>
</>
:null

}

  </div>
}
export default Tab;
