import {useState,useEffect} from 'react';
import axios from 'axios';
import Card from './Card';
const Search =({searchFor=0})=>{
//searches for books 0, libraries 1, or both 2

  const [libraries,setLibraries]= useState(null);
  const [books,setBooks]= useState(null);

  useEffect(()=>{
  const getBooksLibraries = async()=>{
    if(searchFor ===0 || searchFor ===2){
      const res = await axios.get('/books');
      console.log(res.data);
      setBooks(res.data)
    }
    if(searchFor > 0){
      const res = await axios.get('/libraries/public/0');
      console.log(res.data);
        setLibraries(res.data);
    }

  }
  getBooksLibraries();
  },[])

return <>
{
  libraries?
  libraries.map(library => <Card key={library.id} prop={library.id} title={library.name} texts={[library.description,`${library.is_public? 'public':'private'}`]} handleClick={()=>navigate(`/library/${library.id}`)}/>)
  :null
}
{
  books?
  books.map(book => <Card key={book.id} prop={book.id} title={book.title} texts={[book.synopsys,`${book.is_public? 'public':'private'}`]} handleClick={()=>navigate(`/book/${book.id}`)}/>)
  :null
}

</>


}
export default Search;
