import { useParams} from "react-router-dom";
import {useState,useEffect} from 'react';
import axios from 'axios';
import Card from './Card';
const UserBooks=()=>{
const { username } = useParams();

const [books,setBooks]= useState(null);

useEffect(()=>{
const getLoggedInUser = async()=>{
  const res = await axios.get(`/users/${username}/books`);
  if(res.data){
    console.log(res.data);
    setBooks(res.data);
  }
  else{
    setBooks(null);
  }

}
getLoggedInUser();
},[])


return<>
<h1>{`${username}'s Books`}</h1>
{
  books?
  books.map(book => <Card key={book.id} prop={book.id} title={book.name} texts={[book.description,`${book.is_public? 'public':'private'}`]} handleClick={viewBook}/>)
  :null
}


</>


}
export default UserBooks;
