import ThemeForm from '../forms/ThemeForm';
import ChangePasswordform from '../forms/ChangePasswordform';
import PopUpForm from '../forms/PopUpForm';
import {useState,useContext} from 'react';
import {useNavigate} from 'react-router-dom';
import { UserContext } from "../context/context";
import useAxios from '../hooks/useAxios';
const Settings =()=>{
  const currentUser = useContext(UserContext);
const [deleteForm,setDeleteForm]=useState(false);

const [reqUser,user] = useAxios(currentUser);

const changePassword =(data)=>{
  reqUser('');
}

const deleteUser =()=>{
  reqUser('delete',`/users/${currentUser.username}`);
useNavigate('/');
}

const showDeleteForm=()=>{
  setDeleteForm(true);
  //set message
}

  return <>
  <ThemeForm/>
  <ChangePasswordform/>

  <button type="button" onClick={showDeleteForm} name="button">Delete Account</button>

  {deleteForm?
    <PopUpForm
    closeForm={()=>setDeleteForm(false)}
      submit={deleteUser}
      inputs={[
        { title: "Password", name: "password" },
      ]}
      initData={{
        password: '',
      }}
      submitText="Im sure, Delete Account!"
    />:null
  }

  </>

}
export default Settings;
