import { useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import useAxios from "./hooks/useAxios";
import axios from "axios";
import Card from "./Card";
const Search = ({ searchFor = 0 }) => {
  const navigate = useNavigate();

  const [reqLibraries, libraries, setLibraries] = useAxios(null);
  const [reqBooks, books, setBooks] = useAxios(null);

  useEffect(() => {
    if (searchFor === 0 || searchFor === 2) {
      reqBooks("get", "/books", "books");
    }
    if (searchFor > 0) {
      reqLibraries("get", "/libraries/public/0", "libraries");
    }
  }, []);

  return (
    <>
      {libraries
        ? libraries.map((library) => (
            <Card
              key={library.id}
              title={library.name}
              texts={[
                library.description,
                `${library.is_public ? "public" : "private"}`,
              ]}
              handleClick={() => navigate(`/library/${library.id}`)}
            />
          ))
        : null}
      {books
        ? books.map((book) => (
            <Card
              key={book.id}
              title={book.title}
              texts={[
                book.synopsys,
                `${book.is_public ? "public" : "private"}`,
              ]}
              handleClick={() => navigate(`/book/${book.id}`)}
            />
          ))
        : null}
    </>
  );
};
export default Search;
