import { useParams,useNavigate} from "react-router-dom";
import {useState,useEffect} from 'react';
import useAxios from './hooks/useAxios';
import axios from 'axios';
import Card from './Card';
const UserBooks=()=>{
const navigate = useNavigate();
const { username } = useParams();

const [books,setBooks]= useState(null);

useEffect(()=>{
const getLoggedInUser = async()=>{
  const res = await axios.get(`/users/${username}/books`);
  if(res.data.books){
    console.log(res.data);
    setBooks(res.data.books);
  }
  else{
    setBooks(null);
  }

}
getLoggedInUser();
},[])


return<>
<h1>{`${username}'s Books`}</h1>
{
  books?
  books.map(book => <Card key={book.id} title={book.title} texts={[book.synopsys,`${book.is_public? 'public':'private'}`]} handleClick={()=>navigate(`/book/${book.id}`)}/>)
  :null
}

</>


}
export default UserBooks;
