import { useParams} from "react-router-dom";
import useFields from '../hooks/useFields';
import {useState,useEffect} from 'react';
import axios from 'axios';
const NewLibraryBook =({addBook})=>{
const { id } = useParams();

  const [books,setBooks]= useState(null);

  useEffect(()=>{
  const getLoggedInUser = async()=>{
    const res = await axios.get(`/users/${username}/libraries/`);
    if(res.data){
      console.log(res.data);
      setBooks(res.data);
    }
    else{
      setBooks(null);
    }

  }
  getLoggedInUser();
  },[])





  const initData = books? books.map(book => book.id):null;

const[formData,handleChange,resetFormData] = useFields({...initData});


const handleSubmit=(event)=>{
  event.preventDefault();
  addBook(formData);
  resetFormData();
}

return <form className="LoginForm"  onSubmit={handleSubmit}>
{books? books.map(book => <label key={book.id} >{book.title}<input type="checkBox" name={book.id} value={formData[book.id]} onChange={handleChange}/></label>):<h2>No books to add!</h2>}
  <button type="submit" name="button">Save</button>
</form>



}
export default NewLibraryBook;
