import useFields from '../hooks/useFields';
import './SignUpForm.css'
const SignUpForm =({signUp})=>{

  const initData ={
username: '',
password: '',
first_name: '',
last_name: '',
is_pro_user: false
  }


  const[formData,handleChange,resetFormData] = useFields(initData);


  const handleSubmit=()=>{
    signUp(formData);
    resetFormData();
  }


return <form className="LoginForm" onSubmit ={handleSubmit} >
  <label><input type="text" name="username" value={formData.username} onChange={handleChange}/></label>
  <label><input type="text" name="password" value={formData.password} onChange={handleChange}/></label>
  <label><input type="text" name="first_name" value={formData.first_name} onChange={handleChange}/></label>
  <label><input type="text" name="last_name" value={formData.last_name} onChange={handleChange}/></label>
  <label><input type="checkbox" name="is_pro_user" value={formData.is_pro_user} onChange={handleChange}/></label>
  <button type="submit" name="button">Log In</button>
</form>



}
export default SignUpForm;
