import cover from "../sprites/OpenBook1.png";
import page from "../sprites/OpenBookPages.png";

import closedBook from "../sprites/BookMedival.png";
import bookClosed from "../sprites/bookClosed.png";


import ink from "../sprites/ink2.png";
import inkOutline from "../sprites/ink3.png";


import useFields from "../hooks/useFields";
import { useParams } from "react-router-dom";
import {useState} from 'react';
//a form to add a new book
const NewBookForm = ({ addBook }) => {
  const { id } = useParams();


  let initData = {
    title: "",
    synopsys: "",
    text_color: "#000000",
    cover_color: "#ffffff",
    theme: 0,
    is_public: false,
  };

  //
  // const [formData,setFormData] = useState(initState);
  //
  // const handleChange =(event)=>{
  //   let {name,value}=event.target;
  //   if(event.target.type === 'checkbox'){
  //     value = event.target.checked;
  //   }
  //
  //   setFormData((state)=>({...state,[name]: value}));
  // }
  //
  // const resetState =()=>{
  //   setState(initState);
  // }



  const [formData, handleChange, resetFormData] = useFields(initData);

  const handleSubmit = (event) => {
    event.preventDefault();
    addBook(formData, id);
    resetFormData();
  };

  return (
    <form className="NewBookForm" onSubmit={handleSubmit}>
    {
      formData? <>
      <img
      className="WritingPage_closedBook"
      src={closedBook}
      alt="closedBook"
    />
    <img
      style={{ backgroundColor: formData.cover_color}}
      className="WritingPage_closedBook  image_color"
      src={bookClosed}
      alt="closedBook"
    /></>
  :null}
      <label>
        title
        <input
          required
          type="text"
          name="title"
          value={formData.title}
          onChange={handleChange}
        />
      </label>
      <label>
        synopsys
        <input
          type="text"
          name="synopsys"
          value={formData.synopsys}
          onChange={handleChange}
        />
      </label>
      <label>
        Title text color
        <input
          type="color"
          name="text_color"
          value={formData.text_color}
          onChange={handleChange}
        />
      </label>
      <label>
        Cover color
        <input
          type="color"
          name="cover_color"
          value={formData.cover_color}
          onChange={handleChange}
        />
      </label>
      <label>
        Public
        <input
          type="checkbox"
          name="is_public"
          value={formData.is_public}
          onChange={handleChange}
        />
      </label>
      <button type="submit" name="button">
        Write!
      </button>
    </form>

  );
};
export default NewBookForm;
