import cover from './sprites/OpenBook1.png'
import page from './sprites/OpenBookPages.png'
import {useState,useEffect} from 'react';
import InputPage from './InputPage'
import Page from './Page'
import axios from 'axios';
import './css/Book.css';
const Book =({areReading,bookId})=>{

const [pages,setPages]=useState([]);
const [pageNum,setPageNum]=useState([1,2]);//maybe later keep track of what page they were on last and add it to the book in the db


useEffect(()=>{
const getPages = async()=>{
  const res = await axios.get(`/books/${bookId}/pages`);
  if(res.data.pages.length>0){
    setPages((n)=> n= res.data.pages);
  }
  else if(res.data.pages.length<=0 && !areReading){
    addPage();
  }

}
getPages();
},[])

const updatePage=async(text,pageId)=>{
  let res = await axios.patch(`/books/${bookId}/pages/${pageId}`,{text:text});
  console.log(res.data.id);
  if(res.data.id){
    return res.data;
   // setPages((n)=> n= pages.map(page=> page.id === res.data.id? res.data: page));

  }
}


const changePage = async(page,num)=>{
  // debugger;
let pageInfo =[];
let newPageNum = pageNum;

  if(page.text != pages[pageNum[0]-1].text){
    let res = await updatePage(page.text,pages[pageNum[0]-1].id);
    pageInfo.push(res);
  }

  if(page.text2){
    if(page.text2 != pages[pageNum[1]-1].text){
      let res = await updatePage(page.text2,pages[pageNum[1]-1].id);
      pageInfo.push(res);
    }
  }

  let newPages =[]
if(pageInfo && pages.length>0){
   newPages= pages.map(page=>{
     if(page.id === pageInfo[0].id) return pageInfo[0];
     if(pageInfo[1]){
       if(page.id === pageInfo[1].id) return pageInfo[1];
     }
     return page;
  });
}else{
  newPages=pageInfo;
}

  if(pageNum[1]+num > pages.length){
    if(!areReading){
      await addPage();
      newPageNum = [pageNum[0]+num,pageNum[1]+num];

    }
  }
  else if(pageNum[0]+num > 0){
    newPageNum = [pageNum[0]+num,pageNum[1]+num];
  }
  setPages((n)=> n=newPages);
  setPageNum((n)=> n= newPageNum);
}


const addPage =async(data)=>{
 let res = await axios.post(`/books/${bookId}/pages`,{num_of_pages:2,page_num:pageNum[0]});
if(res.data.pages){
  setPages((n)=> n= [...pages,...res.data.pages]);
}
}

return <div className ='Book'>

<img className ='Book_cover' src={cover} alt="cover"/>
<img className ='Book_pages' src={page} alt="pages"/>


{
  pages.length>0?
    areReading?
    <>
    <Page pageNum={pageNum[0]} changePage={changePage} text={pages[pageNum[0]-1].text}/> <Page pageNum={pageNum[1]} changePage={changePage} text={pages[pageNum[1]-1].text}/>
    </>
    : <>
    <InputPage pageNum={pageNum[0]} changePage={changePage} text={[pages[pageNum[0]-1].text,pages[pageNum[1]-1].text]} isTwoPages={true}/>
    </>
  : null
}


</div>
}

export default Book;
