import useFields from './hooks/useFields';
import './css/InputPage.css';
const InputPage =({pageNum,changePage,text=''})=>{

console.log('text',text);
if(typeof text != 'string'){
  text ='';
}

const[page,handleChange,resetpage] = useFields({text:text});

const handleClick=()=>{
  changePage(page);
  resetpage();
}

console.log('input text',page);
return <div className ='InputPage'>
<textarea name="text" rows="27" cols="33" value={page.text} onChange={handleChange}></textarea>
<div className ='ChangePage' onClick ={handleClick}></div>
</div>
}
export default InputPage;
