import { useState } from "react";
import axios from "axios";

const useAxios=(initalVal)=>{
  const [state,setState] = useState(initalVal)

  const request=async(type,url,key,data)=>{
    let res='';

    switch (type){
      case 'get':
        res = await axios.get(url, {headers:{credentials: 'include'}});
        break;
        case 'post':
       res = await axios.post(url,{credentials: 'include',...data});
          break;
          case 'patch':
             res = await axios.patch(url,{credentials: 'include',...data});
            break;
            case 'delete':
             res = await axios.delete(url,{credentials: 'include'});
              break;
      default:
      break;
    }
//     console.log('request data',data);
// console.log('request type',type);
// console.log('request res',res);

    if(res.data[key]){
      state?
        setState(...state,...res.data[key])
      :
        setState(res.data[key]);

      return {data: res.data[key]}

    }
    else if(res.data.message){
      console.log('message');
      if(res.status != 201 && res.status != 200)setState(initalVal);
      return res.data;
    }
    if(res.status === 404){
      return {message:`No ${key} found!`};
    }
  }

return [request,state,setState];
}

