import PopUpForm from "../forms/PopUpForm";
import { useState, useEffect } from "react";
import useToggle from "../hooks/useToggle";
import bookmark from "../sprites/bookMark.png";
import axios from "axios";
import "../css/CharacterBook.css";
import NotePage from "../notes/NotePage";
import Tab from './Tab';



///shows all the bookmarks for a book and lets you add a new one
const BookMark = ({ bookId, username,currPageId}) => {


  const [notes, setNotes] = useState([]);
  const [currNote, setCurrNote] = useState(1);
  const [addForm, setAddForm] = useState(false);
  const [editForm, setEditForm] = useState(false);
  const [addExistingForm, setAddExistingForm] = useState(false);

  useEffect(() => {
    const getNotes = async () => {
      const res = await axios.get(`/bookmarks/${bookId}`);
      console.log('get book marks',res);
      if (res.data.bookmarks.length > 0) {
        setNotes((n) => (n = res.data.bookmarks));
      }
    };
    getNotes();
  }, []);


  const changeNote = (num) => {
    let newNote = num;
    if (newNote < 0) newNote = 0;
    if (newNote >= notes.length) newNote = notes.length-1;
    setCurrNote(newNote);
  };

  const addNote = async (data,pageId) => {
    const res = await axios.post(`/bookmarks/${bookId}/${pageId}`, data);
console.log('add book mark', res);
    if (res.data.bookmark) {
      setNotes((n) => (n = [...notes, res.data.bookmark]));
    }
  };

  const deleteNote = async (pageId) => {
    const res = await axios.delete(`/bookmarks/${bookId}/${pageId}`);
    console.log(res);
    if (res.data.message === "Deleted!") {
      if (currNote) {
        if (notes[currNote].id === pageId) {
          changeNote(currNote-1);
        }
      }
      let updatedNotes = notes.map((note) => {
        if (note.id != pageId) return note;
      });
      if (!updatedNotes[0]) updatedNotes = [];
      setNotes((n) => (n = updatedNotes));
    }
  };

  const editNote = async (data,pageId) => {
    const res = await axios.patch(`/bookmarks/${bookId}/${pageId}`, {...data,page_id:currPageId,book_id: bookId});
    if (res.data.bookmark) {
      setNotes(
        (n) =>
          (n = notes.map((note) => (note.id != pageId ? note : res.data.bookmark)))
      );
    }
  };



  return (
    <>
   <img className="CharacterBook_Image" src={bookmark} alt="bookmark" />

      {editForm ? (
        <PopUpForm
          closeForm={() => setEditForm(false)}
          submit={editNote}
          inputs={[
            { title: "text", name: "text" },
          ]}
          initData={{
            text: "",
          }}
          submitText="Edit"

        />
      ) : null}


      <div className="CharacterBook">


        {notes.length > 0 ?  (
          notes.map((note, inx) => (
            <Tab info={note} page_id={currPageId} handleClick={()=>changeNote(inx)} editTab={editNote} deleteTab={deleteNote}/>
          ))
        ) :null}
      </div>



      {addForm ? (
        <PopUpForm
          closeForm={() => setAddForm(false)}
          submit={addNote}
          inputs={[
            { title: "text", name: "text" },
          ]}
          initData={{
            text: "",
          }}
          submitText="Add"
        />
      ) : (
        <>
          <button
            type="button"
            className="CharacterBook_button"
            onClick={() => setAddForm(true)}
            name="button"
          >
            New Bookmark
          </button>
        </>
      )}

    </>
  );
};
export default BookMark;
