    await updatePage(page.text);
  }

  if(page.text2){
    if(page.text2 != pages[pageNum[1]-1].text){
      await updatePage(page.text2);
    }
  }

  let newPages = pages.map(page=> page.id === res.data.id? res.data: page);

  if(pageNum[1]+num > pages.length){
    if(!areReading){
      await addPage();
      setPageNum((n)=> n=[pageNum[0]+num,pageNum[1]+num]);
    }
  }
  else if(pageNum[0]+num > 0){
    setPageNum((n)=> n=[pageNum[0]+num,pageNum[1]+num]);
  }



}


const addPage =async(data)=>{
