import {useState,useEffect} from 'react';
import InputPage from './InputPage'
const Page =({pageNum,page,changePage,savePage,areReading=false,isTwoPages=true})=>{

const [input,setInput]=useState(false);


const handleClick=(num)=>{
  changePage(num,page);
}

return <div className ='Page'>
<p className ='Page_text' onClick={()=>setInput(true)} >{page.text}</p>
<p className ='Page_text2'>{page.text2}</p>

{input?
<InputPage changePage={changePage} savePage={savePage} text={page}/>
:null
}

<p>{pageNum}</p>
{isTwoPages? <p>{pageNum+1}</p>:null}

<div className ='ChangePage' onClick ={changePage}></div>

{
  input?
  null
  :<>
  <div className ='ChangePageBack' onClick ={()=>handleClick(-2)}></div>
  <div className ='ChangePageNext' onClick ={()=>handleClick(2)}></div>
  </>
}

</div>
}

export default Page;

