import useFields from './hooks/useFields';
import './css/InputPage.css';
const InputPage =({pageNum,changePage,text,isTwoPages=false})=>{

const init ={
  text:text[0]
}

if(isTwoPages) init['text2']=text[1];

const[page,handleChange,resetpage] = useFields(init);

const handleClick=(num)=>{
  changePage(page,num);
  resetpage();
}

return <div className ='InputPage'>
<textarea name="text" rows="27" cols="33" value={page.text} onChange={handleChange}></textarea>
{isTwoPages? <textarea name="text2" rows="27" cols="33" value={page.text2} onChange={handleChange}></textarea>:null}
<p>{pageNum}</p>
{isTwoPages? <p>{pageNum+1}</p>:null}


<div className ='ChangePageBack' onClick ={()=>handleClick(-2)}></div>
<div className ='ChangePageNext' onClick ={()=>handleClick(2)}></div>
</div>
}
export default InputPage;
