      reqPages('patch',`/books/${bookId}/pages/${pageId}`,'page',{text:data.text});
      reqPages('patch',`/books/${bookId}/pages/${pageId2}`,'page',{text:data.text2});
    }

  }



  return <div className ='Book'>

  <img className ='Book_cover' src={cover} alt="cover"/>
  <img className ='Book_pages' src={page} alt="pages"/>


  {
