      areReading?
      <>
      <Page pageNum={currPage[0]+1} changePage={changePage} text={pages[currPage[0]].text}/> <Page pageNum={currPage[1]} changePage={changePage} text={pages[currPage[1]].text}/>
      </>
      : <>
      <InputPage pageNum={currPage[0]+1} changePage={changePage} text={[pages[currPage[0]].text, pages[currPage[1]].text]} isTwoPages={true}/>
      </>
    : null
  }


