import { useState } from "react";
import axios from "axios";

const useGet=(url,key)=>{
  const [state,setState] = useState()
  const getData=async()=>{
    const res = await axios.get(url,{credentials: 'include'});
    if(res.data[key]){
      setState(res.data[key])
    }else if(res.data.message){
      return res.data.message;
    }
    if(res.status === 404){
      return `No ${key} found!`;
    }
  }

return [state,setState,getData];
}



const usePost=(url,key,data)=>{
  const [state,setState] = useState()

    const postData=async()=>{
      const res = await axios.post(url,{credentials: 'include',...data});
      if(res.data[key]){
        setState(res.data[key])
      }else if(res.data.message){
        return res.data.message;
      }
      if(res.status === 404){
        return `No ${key} found!`;
      }
    }

  return [state,setState,postData];
}

const usePatch=(url,key,data)=>{
    const [state,setState] = useState()

    const patchData=async()=>{
      const res = await axios.patch(url,{credentials: 'include',...data});
      if(res.data[key]){
        setState(res.data[key])
      }else if(res.data.message){
        return res.data.message;
      }
      if(res.status === 404){
        return `No ${key} found!`;
      }

    }

  return [state,setState,patchData];
}


const useDelete=async(url,key)=>{
      const res = await axios.delete(url,{credentials: 'include'});
      if(res.data.message){
        return res.data.message;
      }
      if(res.status === 404){
        return `No ${key} found!`;
      }
}


export { useGet,usePost,usePatch,useDelete };
