import { useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import useAxios from "./hooks/useAxios";
import axios from "axios";
import Card from "./Card";
import NewLibraryBook from './forms/NewLibraryBook';
const Search = ({ currentUser,searchFor = 0 }) => {
  const navigate = useNavigate();
  const [reqLibraries, libraries, setLibraries] = useAxios(null);
  const [reqBooks, books, setBooks] = useAxios(null);
const [addForm,setAddForm] = useState(false);

const username = currentUser.username;

  useEffect(() => {
    if (searchFor === 0 || searchFor === 2) {
      reqBooks("get", "/books", "books");
    }
    if (searchFor > 0) {
      reqLibraries("get", "/libraries/public/0", "libraries");
    }
  }, []);

  const addBook=async(data)=>{
  const res = await reqBooks('post',`/users/${username}/libraries/books`,'',{libraries:Object.keys(data.libraries),books:[data.bookId]})
  setAddForm(false)
  }


  return (
    <>
      {libraries
        ? libraries.map((library) => (
            <Card
              key={library.id}
              title={library.name}
              texts={[
                library.description,
                `${library.is_public ? "public" : "private"}`,
              ]}
              handleClick={() => navigate(`/library/${library.id}`)}
            />
          ))
        : null}
      {books
        ? books.map((book) => (<div key={book.id}>
            <Card
              title={book.title}
              texts={[
                book.synopsys,
                `${book.is_public ? "public" : "private"}`,
              ]}
              handleClick={() => navigate(`/book/${book.id}`)}
              />
              <button
              key={book.id}
                type="button"
                onClick={()=>setAddForm(true)}
                name="button"
              >
                Add To Library
              </button>
              {
                addForm?
                <div className='Library_Add_Book'>
                <NewLibraryBook  bookId={book.id} addBook={addBook} username={username} title='name' isBooks={false}/>
                <button type="button" onClick={()=>setAddForm(false)} name="button">Cancle</button>
                </div>
                :null
              }
            </div>
          ))
        : null}
    </>
  );
};
export default Search;
